/**
 * WS连接管道
 * ws 服务端用来保存 与客户端连接 的 kvpair，key 为 uri，value 为 connObj
 */
class WSConnection { 
  /**
   * 连接通道 的对象
   * @param key 为连接对象取的key，一般用对应服务端的 URI
   * @param connObj 客户端连接通道
   */
  constructor(key, connObj) { 
    this.key = key;
    this.connObj = connObj;
  }

  sendMsg(msg) {
    this.connObj.sendUTF(msg);
  }
}

module.exports = WSConnection;